mapper.insert:
INSERT INTO creation_class (
	code,
	fk_name,
	fk_description
) VALUES (
	#{code},
	#{fkName},
	#{fkDescription}
)


select next from _sequences where name = 'ID_CREATION_CLASS_SEQ'

mapper.update:
UPDATE creation_class
SET 
	fk_name 			= #{fkName},
	fk_description		= #{fkDescription}
WHERE 
	id_creation_class	= #{idCreationClass}

mapper.select:
SELECT  COUNT(id_creation_class) AS result 
FROM  creation_class 
WHERE code = #{code}

		AND creation_class.id_creation_class != #{idToExclude}
