mapper.update:
	UPDATE identifier
   	SET
   		fk_label					= #{fkName},
   		acronym 					= #{acronym}
 	WHERE
		identifier.id_identifier 	= #{idIdentifier}

mapper.select:
SELECT  COUNT(id_identifier) AS result
FROM  identifier
WHERE code = #{code}

		AND identifier.id_identifier != #{idToExclude}

mapper.insert:
INSERT INTO identifier_class (
	fk_identifier,
	fk_creation_class
) VALUES (
	#{fkIdentifier},
	#{fkCreationClass}
)
