mapper.select:
SELECT ,
FROM ip_task LEFT OUTER JOIN ip_task_item ON ip_task.id_ip_task = ip_task_item.fk_ip_task LEFT OUTER JOIN ip_task_item_detail ON ip_task_item.id_ip_task_item = ip_task_item_detail.fk_ip_task_item LEFT OUTER JOIN ip_task_detail_ident ON ip_task_detail_ident.fk_ip_task_item_detail = ip_task_item_detail.id_ip_task_item_detail LEFT OUTER JOIN ip_task_detail_territory ON ip_task_detail_territory.fk_ip_task_item_detail = ip_task_item_detail.id_ip_task_item_detail LEFT OUTER JOIN ip_task_detail_name ON ip_task_detail_name.fk_ip_task_item_detail = ip_task_item_detail.id_ip_task_item_detail LEFT OUTER JOIN ip_task_detail_affiliation ON ip_task_detail_affiliation.fk_ip_task_item_detail = ip_task_item_detail.id_ip_task_item_detail LEFT OUTER JOIN ip_task_detail_aff_domain ON ip_task_detail_aff_domain.fk_ip_task_detail_affiliation = ip_task_detail_affiliation.id_ip_task_detail_affiliation
WHERE ip_task_item.item_status = #{statusCode}

SELECT 		count(*)
FROM		ip_task


			AND ip_task.task_code = #{taskCode}




			AND ip_task.task_status = #{status}

