	#{fkName},
	#{fkDescription}
)


	select next from _sequences where name = 'ID_IPI_ROLE_SEQ'

mapper.update:
UPDATE ipi_role
SET 
	fk_name 			= #{fkName},
	fk_description		= #{fkDescription}
WHERE 
	id_ipi_role			= #{idIpiRole}

mapper.select:
SELECT  COUNT(id_ipi_role) AS result 
FROM  ipi_role 
WHERE code = #{code}

		AND ipi_role.id_ipi_role != #{idToExclude}

