mapper.insert:
INSERT INTO right_type (
		code,
		fk_name,
		fk_description
	) VALUES (
		#{code},
		#{fkName},
		#{fkDescription}
	)

	select next from _sequences where name = 'ID_RIGHT_TYPE_SEQ'

mapper.update:
UPDATE right_type
SET
	fk_name 			= #{fkName},
	fk_description		= #{fkDescription}
WHERE 
	id_right_type 	= #{idRightType}

mapper.select:
SELECT  COUNT(id_right_type) AS result 
FROM  right_type 
WHERE code = #{code}

		AND right_type.id_right_type != #{idToExclude}

mapper.insert:
INSERT INTO right_type_class (
	fk_right_type,
	fk_creation_class
) VALUES (
	#{fkRightType},
	#{fkCreationClass}
)
